from lxml import htmlimport urllibimport requestsimport re#对文件名变化，得到大的图片def ChangeImageName(imagename):    # batou.gif    # s_DSC01025.jpg    # joisel_part1_s.jpg    # santaclaus_s.jpg    if re.findall("gif", imagename):        # print(imagename)        return imagename.replace('gif','jpg')    if re.findall("_s", imagename):        # print(imagename)        return imagename.replace('_s','')    if re.findall("s_", imagename):        # print(imagename)        return imagename.replace('s_','')    #其他情况    return imagename# response = urllib.request.urlopen('https://www.origamihouse.jp/book/original/quentin/quentin.html')response = urllib.request.urlopen('https://www.origamihouse.jp/book/original/insects2/insects2.html')# response = urllib.request.urlopen('https://www.origamihouse.jp/book/original/kamiya3/kamiya3.html')result = response.read().decode('Shift_JIS')html = html.fromstring(str(result))title = html.xpath('//tr/td')title = html.xpath('//div/table//tr/td[1]//img')print(title[0].attrib)print(title[0].get('src'))# images = html.xpath('//table//tr//td//a')images = html.xpath('//a//img')for image in images:    # print(image.attrib)    #查找不以..开头的内容    #查找不以.和http开头的内容    print(image.attrib)    # print(image.onclick)    # if re.findall(r"^[^\.][^https]",image.get('src')):    #     print(image.get('src'))# print(images[0].attrib)# print(images[0].get('src'))# print(images[1].attrib)# print(images[1].get('src'))