# 提取网页中的链接import reimport urllibfrom lxml import htmlimport requestsimport lxmlf = open('./result.csv','r')# print(f.read())lines = f.readlines()for line in lines:    result = re.match('https:\/\/www\.origamihouse\.jp\/book\/original\/[^oh].*', line)    # result = re.match('https:\/\/www\.origamihouse\.jp\/book\/original.*', line)    # print(result)    if result:        # 获取新书的url地址        print('新的页面: ', line.split(',')[0])        new_page = line.split(',')[0]        # New Page is:  https://www.origamihouse.jp/book/original/nishikawa/nishikawa.html        url_first_position = new_page.rfind('/')        new_url_first = new_page[:url_first_position]        # https://www.origamihouse.jp/book/original/nishikawa        # print(new_url_first)        # 从新的url里获取图片        response = urllib.request.urlopen(new_page)        result = response.read().decode('Shift_JIS')        html = lxml.html.fromstring(str(result))        title = html.xpath('//tr/td')        # //表示子节点或者孙节点        title = html.xpath('//div[3]/table[1]//tr/td[1]//img')        try:            print('获取到的属性:', title[0].attrib)            print('获取到的链接内容: ', title[0].get('src'))            print('主图片的链接: ', new_url_first+'/'+title[0].get('src'))            print('----------------------------------------')        except IndexError:            passf.close()