import recontent = 'Hello 123 4567 World_This is a Regex Demo'print(len(content))#match函数的第一个参数是正则表达式，第二个参数是字符串result = re.match('^Hello\s\d\d\d\s\d{4}\s\w{10}',content)print(result)print(result.group())print(result.span())content = 'Hello 1234567 World_This is a Regex Demo'result = re.match('^Hello\s(\d+)\sWorld',content)print(result)print(result.group())print(result.group(1))print('0 is ',result.group(0))# print(result.group(3))print(result.span())#准备要匹配的字符串html = ''#匹配以li开头，然后寻找一个标志符active，提取singer的属性值，然后匹配超链接的内容result = re.search('<li.*?active.*?singer="(.*?)">(.*?)</a>',html, re.S)if result:    print(result.group(1),result.group(2))