#抓取网页#过滤有用的信息，然后把这些信息保存到txt文件中import requestsfrom pyquery import PyQuery as pqurl='https://www.zhihu.com/explore'headers = {    'User-Agent': 'Mozilla/5.0 (Macintosh; Intel Mac OS X 10_11_4) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/52.0.2743.116 Safari/537.36'}html = requests.get(url, headers=headers).textprint(html)doc = pq(html)items = doc('.explore-tab .feed-item').items()for item in items:    question = item.find('h2').text()    author = item.find('.author-link-line').text()    answer = pq(item.find('.content').html()).text()    #以追加的形式写入文件    file = open('explore.txt','a',encoding = 'utf-8')    file.write('\n'.join([question,author,answer]))    file.write('\n' + '=' *50 +'\n')    file.close()