# 使用tesserocr识别图像中的文字from selenium import webdriver# browser = webdriver.PhantomJS()# browser.get('https://www.google.com')# print(browser.current_url)# 测试 tesseractimport tesserocrfrom PIL import Image# 获取图片image = Image.open('1-26.jpg')# 识别英文单词# 输出识别结果print(tesserocr.image_to_text(image))# 输出识别结果print(tesserocr.file_to_text('1-26.jpg'))# -----------------------------------------------------------# 识别中文验证码imges_chinese = Image.open('checkcode.png')# 需要添加语言参数lang="chi_sim"print(tesserocr.image_to_text(imges_chinese, lang="chi_sim"))# -----------------------------------------------------------# -----------------------------------------------------------# 识别中文文章imges_chinese = Image.open('chinese_article.png')# 需要添加语言参数lang="chi_sim"print(tesserocr.image_to_text(imges_chinese, lang="chi_sim"))# -----------------------------------------------------------# -----------------------------------------------------------# 识别日语验证码imges_japanese = Image.open('checkcode_jap.png')print(tesserocr.image_to_text(imges_japanese, lang="jpn"))# -----------------------------------------------------------# -----------------------------------------------------------# 识别日语文章article_japanese = Image.open('article_jap.png')print(tesserocr.image_to_text(article_japanese, lang="jpn"))# -----------------------------------------------------------# -----------------------------------------------------------# 测试数据库链接import pymysqlprint('pymysql version is ',pymysql.VERSION)# -----------------------------------------------------------# -----------------------------------------------------------# 测试爬虫框架pyspider# import pyspider