#python中的函数abs(100)print(abs(-100))print(float(12.3444))print(int(12.3444))def cmp(a, b):    return (a >b) -(a < b)print(cmp(3,2))print(int(3>2))#返回多个值import mathdef move(x, y, step, angle=0):    nx=x + step* math.cos(angle)    ny=y + step*math.sin(angle)    return nx, nyx, y = move(100, 100, 60, math.pi /6)print(x,y)def power(x):    return x * xprint(power(567))def enroll(name,gender):    print('name',name)    print('gender',gender)enroll('Fan Xin','woman')enroll('Fan XinXin','woman')def calc(numbers):    sum = 0;    for i in numbers:        print(i)#可变参数def calc2(*number):    sum = 0    for n in number:        sum = sum + n*n    return sumcalc((1,3,4,5))enroll('wang','hello')calc([1,2,3])enroll('wang','hello')nums = [1,2,3]print(calc2(*nums))print(calc2(1,2,3))print(calc2())print(calc((1,2,3,5)))# print(calc2((1,2,3,5)))print("-----------------------------------------------")#关键字函数def person(name, age, **kw):    print(name, age, kw)print(person('Michael',30))print(person('Michael',30,city='Beijing'))print("-----------------------------------------------")