from tkinter import *import tkinter.messagebox as messagebox#从Frame中派生出一个叫Application的类class Application(Frame):    def __init__(self, master = None):        Frame.__init__(self,master)        self.pack()        self.createWidgets()    def createWidgets(self):        # self.helloLabel = Label(self,text='Hello')        self.nameInput = Entry(self)        self.nameInput.pack()        #pack方法表示把该ｗｉｄｇｅｔ加入到父容器中        # self.helloLabel.pack()        # self.quitButton = Button(self, text='Quit', command=self.quit)        self.alertButton = Button(self, text='hello', command=self.hello)        self.alertButton.pack()    def hello(self):        name = self.nameInput.get() or 'world'        messagebox.showinfo('Message','Hello, %s' %name)#app = Application()#设置窗口标题app.master.title('Hello')#主消息循环app.mainloop()