from  datetime import datetimenow = datetime.now()print(now)#指定日期和时间dt = datetime(2015,4,19,12,20)print(dt)print(dt.timestamp())#datetime加减from datetime import timedeltaprint(now + timedelta(hours= 10))#时区转换from datetime import timezoneutc_dt=datetime.utcnow().replace(tzinfo=timezone.utc)tokyo_dt=utc_dt.astimezone(timezone(timedelta(hours=9)))beijing_dt=utc_dt.astimezone(timezone(timedelta(hours=8)))print(tokyo_dt)print(beijing_dt)#collectionsfrom collections import namedtuplePoint = namedtuple('Point',['x','y'])p = Point(1,2)print(p.x,p.y)