#面向对象编程__author__='Fan Xin'__date__='2019-06-17'#定义类Studentclass Student(object):    def __init__(self, name, score):        self.__name = name        # self.name = name        self.score = score    def print_score(self):        print(self.name, self.score)    def get_name(self):        return self.__name#生成实例bart = Student('abc',59)bart2 = Student('lisa',87)#使用实例的方法# bart.print_score()# bart2.print_score()print(bart.get_name())#打印实例的地址print(bart)print(Student)#获取对象信息print(type(123))print(dir(bart))print(dir('abc'))