#递归函数def fact(n):    if n==1:        return 1    return n* fact(n-1)print(fact(1))print(fact(3))print(fact(5))#判断是否是可以迭代的类型from collections import Iterableprint(isinstance('abc',Iterable))print("-----------------------------------------------")for i,value in enumerate(['A','B','C']):    print(i,value)print("-----------------------------------------------")for x, y in [(1,1),(2,4),(3,9)]:    print(x,y)print("-----------------------------------------------")for x in [(1,1),(2,4),(3,9)]:    print(x)print(range(1,11))import osprint([d for d in os.listdir('.')])print(sorted([36,5,-12,9,-21]))print(sorted([36,5,-12,9,-21], key=abs))print(sorted([36,5,-12,9,-21], key=abs))